import React, { useState, useEffect } from 'react';

// Sidebar Component
const Sidebar = ({ setPage }) => {
  return (
    <div style={sidebarStyle}>
      <div style={{ ...sidebarHeader, paddingBottom: '10px' }}>
        <img src="/logofront.png" alt="DeepShield Logo" style={{ width: '100%', maxHeight: '80px', objectFit: 'contain' }} />
      </div>

      <ul style={sidebarList}>
        <li style={sidebarItem} onClick={() => setPage('whitelist')}>
          <div style={sidebarIconContainer}>
            <img src="/whitelist.png" alt="Whitelist" style={iconStyle} />
            <span>Whitelist</span>
          </div>
        </li>
        <li style={sidebarItem} onClick={() => setPage('blacklist')}>
          <div style={sidebarIconContainer}>
            <img src="/blacklist.png" alt="Blacklist" style={iconStyle} />
            <span>Blacklist</span>
          </div>
        </li>
        <li style={sidebarItem} onClick={() => setPage('Sample1')}>
          <div style={sidebarIconContainer}>
            <span>Sample1</span>
          </div>
        </li>
        <li style={sidebarItem} onClick={() => setPage('Sample11')}>
          <div style={sidebarIconContainer}>
            <span>Sample11</span>
          </div>
        </li>
      </ul>
    </div>
  );
};

// Example inline styles (can be replaced with CSS or Tailwind)
const sidebarStyle = {
  width: '220px',
  height: '100vh',
  backgroundColor: '#0f172a',
  color: 'white',
  padding: '15px 10px',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'start',
  borderRight: '1px solid #1e293b'
};

const sidebarHeader = {
  marginBottom: '20px',
  width: '100%',
};

const sidebarList = {
  listStyleType: 'none',
  padding: 0,
  width: '100%'
};

const sidebarItem = {
  display: 'flex',
  alignItems: 'center',
  padding: '10px',
  cursor: 'pointer',
  transition: 'background-color 0.2s ease',
};

const sidebarIconContainer = {
  display: 'flex',
  alignItems: 'center',
  gap: '10px',
};

const iconStyle = {
  width: '20px',
  height: '20px',
};

export default Sidebar;
