import React, { useState, useEffect } from 'react';

function App() {
  const [page, setPage] = useState('login');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const savedUsers = JSON.parse(localStorage.getItem('users')) || [];
    setUsers(savedUsers);
  }, []);

  const saveUsers = (newUsers) => {
    localStorage.setItem('users', JSON.stringify(newUsers));
    setUsers(newUsers);
  };

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
  };

  const validatePassword = (password) => {
    const strong = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/;
    return strong.test(password);
  };

  const handleLogin = (email, password) => {
    const user = users.find(u => u.email === email && u.password === password);
    if (user) {
      setCurrentUser(user);
      setIsLoggedIn(true);
      setPage('dashboard');
    } else {
      alert('Invalid email or password.');
    }
  };

  const handleRegister = (email, password, confirm) => {
    if (!validateEmail(email)) {
      alert('Invalid email format.');
      return;
    }

    if (!validatePassword(password)) {
      alert('Password must be at least 8 characters, contain a number, uppercase and lowercase letter.');
      return;
    }

    if (password !== confirm) {
      alert('Passwords do not match.');
      return;
    }

    if (users.find(u => u.email === email)) {
      alert('Email already registered.');
      return;
    }

    const newUsers = [...users, { email, password }];
    saveUsers(newUsers);
    alert('Account created! You can now log in.');
    setPage('login');
  };

  const handleChangePassword = (current, newPass, confirmPass) => {
    if (currentUser.password !== current) {
      alert('Current password is incorrect.');
      return;
    }

    if (!validatePassword(newPass)) {
      alert('New password must be at least 8 characters, contain a number, uppercase and lowercase letter.');
      return;
    }

    if (newPass !== confirmPass) {
      alert('New passwords do not match.');
      return;
    }

    const updatedUsers = users.map(u =>
      u.email === currentUser.email ? { ...u, password: newPass } : u
    );
    saveUsers(updatedUsers);
    setCurrentUser({ ...currentUser, password: newPass });
    alert('Password changed successfully!');
  };

  const Navbar = () => (
    <nav style={{ backgroundColor: '#cc0000', padding: 10 }}>
      <button onClick={() => setPage('dashboard')} style={navButton}>Dashboard</button>
      <button onClick={() => setPage('settings')} style={navButton}>Settings</button>
      <button onClick={() => { setIsLoggedIn(false); setPage('login'); setCurrentUser(null); }} style={navButton}>Logout</button>
    </nav>
  );

  const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    return (
      <div style={pageStyle}>
        <h2>PLDT Login</h2>
        <input placeholder="Email" onChange={e => setEmail(e.target.value)} /><br />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} /><br />
        <button onClick={() => handleLogin(email, password)}>Login</button><br /><br />
        <span>Don't have an account? <button onClick={() => setPage('register')}>Sign Up</button></span>
      </div>
    );
  };

  const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirm, setConfirm] = useState('');

    return (
      <div style={pageStyle}>
        <h2>Register</h2>
        <input placeholder="Email" onChange={e => setEmail(e.target.value)} /><br />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} /><br />
        <input type="password" placeholder="Confirm Password" onChange={e => setConfirm(e.target.value)} /><br />
        <button onClick={() => handleRegister(email, password, confirm)}>Register</button><br /><br />
        <button onClick={() => setPage('login')}>Back to Login</button>
      </div>
    );
  };

  const Dashboard = () => (
    <div style={pageStyle}>
      <h2>Welcome to your Dashboard</h2>
      <p>Email: {currentUser?.email}</p>
      <p>Plan: Fibr Plus 2099</p>
      <p>Billing Amount: â‚±2,099.00</p>
      <p>Due Date: April 30, 2025</p>
    </div>
  );

  const Settings = () => {
    const [current, setCurrent] = useState('');
    const [newPass, setNewPass] = useState('');
    const [confirmPass, setConfirmPass] = useState('');

    return (
      <div style={pageStyle}>
        <h2>Account Settings</h2>
        <p>Email: {currentUser?.email}</p>
        <p>Plan: Fibr Plus 2099</p>
        <hr />
        <h3>Change Password</h3>
        <input type="password" placeholder="Current Password" onChange={e => setCurrent(e.target.value)} /><br />
        <input type="password" placeholder="New Password" onChange={e => setNewPass(e.target.value)} /><br />
        <input type="password" placeholder="Confirm New Password" onChange={e => setConfirmPass(e.target.value)} /><br />
        <button onClick={() => handleChangePassword(current, newPass, confirmPass)}>Change Password</button>
      </div>
    );
  };

  return (
    <div>
      {isLoggedIn && <Navbar />}
      {!isLoggedIn && page === 'login' && <LoginPage />}
      {!isLoggedIn && page === 'register' && <RegisterPage />}
      {isLoggedIn && page === 'dashboard' && <Dashboard />}
      {isLoggedIn && page === 'settings' && <Settings />}
    </div>
  );
}

const navButton = {
  color: '#fff',
  background: 'transparent',
  border: 'none',
  marginRight: 10,
  cursor: 'pointer',
  fontSize: '16px'
};

const pageStyle = {
  padding: 20,
  fontFamily: 'Arial, sans-serif'
};

export default App;
