import React, { useState, useEffect } from 'react';

function App() {
  const [page, setPage] = useState('login');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    const savedUsers = JSON.parse(localStorage.getItem('users')) || [];
    setUsers(savedUsers);
  }, []);

  const saveUsers = (newUsers) => {
    localStorage.setItem('users', JSON.stringify(newUsers));
    setUsers(newUsers);
  };

  const validateEmail = (email) => /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  const validatePassword = (password) => /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,}$/.test(password);

  const handleLogin = (email, password) => {
    const user = users.find(u => u.email === email && u.password === password);
    if (user) {
      setCurrentUser(user);
      setIsLoggedIn(true);
      setPage('dashboard');
    } else {
      alert('Invalid email or password.');
    }
  };

  const handleRegister = (email, password, confirm) => {
    if (!validateEmail(email)) return alert('Invalid email format.');
    if (!validatePassword(password)) return alert('Password must be at least 8 characters, contain a number, uppercase and lowercase letter.');
    if (password !== confirm) return alert('Passwords do not match.');
    if (users.find(u => u.email === email)) return alert('Email already registered.');
    const newUsers = [...users, { email, password }];
    saveUsers(newUsers);
    alert('Account created! You can now log in.');
    setPage('login');
  };

  const handleChangePassword = (current, newPass, confirmPass) => {
    if (currentUser.password !== current) return alert('Current password is incorrect.');
    if (!validatePassword(newPass)) return alert('New password must be strong.');
    if (newPass !== confirmPass) return alert('New passwords do not match.');
    const updatedUsers = users.map(u => (u.email === currentUser.email ? { ...u, password: newPass } : u));
    saveUsers(updatedUsers);
    setCurrentUser({ ...currentUser, password: newPass });
    alert('Password changed successfully!');
  };

  const Navbar = () => (
    <div style={navbarStyle}>
      <Sidebar />
      <div style={{ marginLeft: '250px', width: '100%' }}>
        <nav style={topNavStyle}>
          <button onClick={() => setPage('dashboard')} style={navButton}>Dashboard</button>
          <button onClick={() => setPage('policies')} style={navButton}>Policies</button>
          <button onClick={() => setPage('settings')} style={navButton}>Settings</button>
          <button onClick={() => { setIsLoggedIn(false); setPage('login'); setCurrentUser(null); }} style={navButton}>Logout</button>
        </nav>
      </div>
    </div>
  );

  const Sidebar = () => (
    <div style={sidebarStyle}>
      <div style={sidebarHeader}>Library</div>
      <div style={sidebarSection}>
        <div style={sidebarTitle}>Previous 7 Days</div>
        <SidebarItem text="React Native to Web" />
        <SidebarItem text="Total Hours Calculation" />
        <SidebarItem text="Expo CLI Fix" />
      </div>
      <div style={sidebarSection}>
        <div style={sidebarTitle}>February</div>
        <SidebarItem text="Install Python in VSCode" />
      </div>
      <div style={sidebarSection}>
        <div style={sidebarTitle}>2024</div>
        <SidebarItem text="Modal Component and Props" />
        <SidebarItem text="React Native Goals App Fix" />
        <SidebarItem text="Thermal Control System Design" />
        <SidebarItem text="Sensors for Drying Seaweed" />
        <SidebarItem text="Carrageenan Drying Temperatures" />
        <SidebarItem text="Alvarezzi Seaweed Soaking Research" />
        <SidebarItem text="Checkout Conveyor Belt Name" />
      </div>
    </div>
  );

  const SidebarItem = ({ text }) => (
    <div style={sidebarItem}>
      {text}
    </div>
  );

  const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    return (
      <div style={pageStyle}>
        <h2>Security System Login</h2>
        <input placeholder="Email" onChange={e => setEmail(e.target.value)} style={inputStyle} /><br />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} style={inputStyle} /><br />
        <button onClick={() => handleLogin(email, password)} style={buttonStyle}>Login</button><br /><br />
        <span>Don't have an account? <button onClick={() => setPage('register')} style={linkButton}>Sign Up</button></span>
      </div>
    );
  };

  const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirm, setConfirm] = useState('');

    return (
      <div style={pageStyle}>
        <h2>Create New Account</h2>
        <input placeholder="Email" onChange={e => setEmail(e.target.value)} style={inputStyle} /><br />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} style={inputStyle} /><br />
        <input type="password" placeholder="Confirm Password" onChange={e => setConfirm(e.target.value)} style={inputStyle} /><br />
        <button onClick={() => handleRegister(email, password, confirm)} style={buttonStyle}>Register</button><br /><br />
        <button onClick={() => setPage('login')} style={linkButton}>Back to Login</button>
      </div>
    );
  };

  const Dashboard = () => {
    const [searchTerm, setSearchTerm] = useState('');

    return (
      <div style={pageStyle}>
        <h2>Dashboard - DoS Monitoring</h2>

        <div style={{ ...cardStyle, marginBottom: 20 }}>
          <h3>Detected DoS Attacks:</h3>
          <p>Last Attack:</p>
        </div>

        <div style={{ ...cardStyle, marginBottom: 20 }}>
          <h3>Current System Status:</h3>
          <p style={{ color: 'lime' }}>Operational</p>
        </div>

        <div style={{ ...searchContainer, marginBottom: 10 }}>
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={e => setSearchTerm(e.target.value)}
            style={searchInput}
          />
        </div>

        <div style={tableContainer}>
          <table style={fixedTableStyle}>
            <thead>
              <tr>
                <th style={thStyle}>#</th>
                <th style={thStyle}>Column 1</th>
                <th style={thStyle}>Column 2</th>
                <th style={thStyle}>Column 3</th>
                <th style={thStyle}>Column 4</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td style={tdStyle}>1</td>
                <td style={tdStyle}>Example</td>
                <td style={tdStyle}>Data</td>
                <td style={tdStyle}>Here</td>
                <td style={tdStyle}>Now</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  };

  const Policies = () => (
    <div style={pageStyle}>
      <h2>Policy Management</h2>
      <div style={cardStyle}>
        <h3>Last Policy Update:</h3>
        <p>Status: <span style={{ color: 'lime' }}>Active</span></p>
        <button style={buttonStyle}>Update Policies Now</button>
      </div>
    </div>
  );

  const Settings = () => {
    const [current, setCurrent] = useState('');
    const [newPass, setNewPass] = useState('');
    const [confirmPass, setConfirmPass] = useState('');

    return (
      <div style={pageStyle}>
        <h2>Settings</h2>
        <div style={cardStyle}>
          <h3>Account: {currentUser?.email}</h3>
          <input type="password" placeholder="Current Password" onChange={e => setCurrent(e.target.value)} style={inputStyle} /><br />
          <input type="password" placeholder="New Password" onChange={e => setNewPass(e.target.value)} style={inputStyle} /><br />
          <input type="password" placeholder="Confirm New Password" onChange={e => setConfirmPass(e.target.value)} style={inputStyle} /><br />
          <button onClick={() => handleChangePassword(current, newPass, confirmPass)} style={buttonStyle}>Change Password</button>
        </div>
      </div>
    );
  };

  return (
    <div style={{ backgroundColor: '#121212', minHeight: '100vh', color: '#fff', display: 'flex' }}>
      {isLoggedIn && <Navbar />}
      {!isLoggedIn && (
        <div style={{ margin: 'auto' }}>
          {page === 'login' && <LoginPage />}
          {page === 'register' && <RegisterPage />}
        </div>
      )}
      {isLoggedIn && page === 'dashboard' && <Dashboard />}
      {isLoggedIn && page === 'policies' && <Policies />}
      {isLoggedIn && page === 'settings' && <Settings />}
    </div>
  );
}

// Styles
const sidebarStyle = {
  width: '250px',
  backgroundColor: '#1f1f1f',
  height: '100vh',
  padding: '20px',
  overflowY: 'auto',
  position: 'fixed',
  top: 0,
  left: 0,
  color: '#ccc'
};
const sidebarHeader = {
  fontSize: '20px',
  fontWeight: 'bold',
  marginBottom: '20px'
};
const sidebarSection = {
  marginBottom: '20px'
};
const sidebarTitle = {
  fontSize: '14px',
  color: '#aaa',
  marginBottom: '10px'
};
const sidebarItem = {
  padding: '5px 0',
  fontSize: '14px',
  cursor: 'pointer'
};
const topNavStyle = {
  backgroundColor: '#1f1f1f',
  padding: 10,
  display: 'flex',
  justifyContent: 'flex-end'
};
const navButton = {
  color: '#fff',
  background: 'none',
  border: 'none',
  marginRight: 20,
  fontSize: '16px',
  cursor: 'pointer'
};
const pageStyle = {
  padding: '30px',
  marginLeft: '250px',
  fontFamily: 'Arial, sans-serif'
};
const cardStyle = {
  backgroundColor: '#1e1e1e',
  padding: 20,
  marginTop: 20,
  borderRadius: 8,
  boxShadow: '0 0 10px rgba(0,0,0,0.5)'
};
const buttonStyle = {
  backgroundColor: '#0078d4',
  color: '#fff',
  border: 'none',
  padding: '10px 20px',
  marginTop: 10,
  cursor: 'pointer',
  borderRadius: 5
};
const inputStyle = {
  marginTop: 10,
  marginBottom: 10,
  padding: 10,
  width: '100%',
  borderRadius: 5,
  border: '1px solid #444',
  backgroundColor: '#2a2a2a',
  color: '#fff'
};
const linkButton = {
  background: 'none',
  color: '#4aa3df',
  border: 'none',
  cursor: 'pointer',
  textDecoration: 'underline',
  fontSize: '14px'
};
const searchContainer = {
  display: 'flex',
  justifyContent: 'flex-start'
};
const searchInput = {
  padding: 10,
  width: '300px',
  borderRadius: 5,
  border: '1px solid #555',
  backgroundColor: '#1f1f1f',
  color: '#fff'
};
const tableContainer = {
  overflowX: 'auto',
  marginTop: 10
};
const fixedTableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  backgroundColor: '#1a1a1a',
  color: '#fff'
};
const thStyle = {
  padding: '12px 15px',
  borderBottom: '1px solid #444',
  textAlign: 'left'
};
const tdStyle = {
  padding: '10px 15px',
  borderBottom: '1px solid #333'
};

export default App;
