import React, { useState } from 'react';

function App() {
  const [page, setPage] = useState('login');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (email, password) => {
    if (email === 'user@pldt.com' && password === '1234') {
      setIsLoggedIn(true);
      setPage('dashboard');
    } else {
      alert('Invalid credentials');
    }
  };

  const Navbar = () => (
    <nav style={{ backgroundColor: '#cc0000', padding: 10 }}>
      <button onClick={() => setPage('dashboard')} style={navButton}>Dashboard</button>
      <button onClick={() => setPage('settings')} style={navButton}>Settings</button>
      <button onClick={() => { setIsLoggedIn(false); setPage('login'); }} style={navButton}>Logout</button>
    </nav>
  );

  const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    return (
      <div style={pageStyle}>
        <h2>PLDT Login</h2>
        <input placeholder="Email" onChange={e => setEmail(e.target.value)} /><br />
        <input type="password" placeholder="Password" onChange={e => setPassword(e.target.value)} /><br />
        <button onClick={() => handleLogin(email, password)}>Login</button>
      </div>
    );
  };

  const Dashboard = () => (
    <div style={pageStyle}>
      <h2>Welcome to your Dashboard</h2>
      <p>Plan: Fibr Plus 2099</p>
      <p>Billing Amount: â‚±2,099.00</p>
      <p>Due Date: April 30, 2025</p>
    </div>
  );

  const Settings = () => (
    <div style={pageStyle}>
      <h2>Account Settings</h2>
      <p>Email: user@pldt.com</p>
      <p>Plan: Fibr Plus 2099</p>
    </div>
  );

  return (
    <div>
      {isLoggedIn && <Navbar />}
      {!isLoggedIn && <LoginPage />}
      {isLoggedIn && page === 'dashboard' && <Dashboard />}
      {isLoggedIn && page === 'settings' && <Settings />}
    </div>
  );
}

const navButton = {
  color: '#fff',
  background: 'transparent',
  border: 'none',
  marginRight: 10,
  cursor: 'pointer',
  fontSize: '16px'
};

const pageStyle = {
  padding: 20,
  fontFamily: 'Arial, sans-serif'
};

export default App;
