const Settings = () => {
  const [currentPasswordForProfile, setCurrentPasswordForProfile] = useState('');
  const [currentPasswordForChange, setCurrentPasswordForChange] = useState('');
  const [newPass, setNewPass] = useState('');
  const [confirmPass, setConfirmPass] = useState('');
  const [newEmail, setNewEmail] = useState(currentUser?.email || '');
  const [newUsername, setNewUsername] = useState(currentUser?.username || '');

  const handleUpdateProfile = () => {
    if (!currentPasswordForProfile) {
      alert('Please enter current password to update profile.');
      return;
    }
    if (currentUser.password !== currentPasswordForProfile) {
      alert('Current password is incorrect.');
      return;
    }
    if (!validateEmail(newEmail)) {
      alert('Please enter a valid email address.');
      return;
    }
    const emailTaken = users.some(u => u.email === newEmail && u.email !== currentUser.email);
    if (emailTaken) {
      alert('This email is already taken.');
      return;
    }

    const updatedUsers = users.map(u =>
      u.email === currentUser.email
        ? { ...u, email: newEmail, username: newUsername }
        : u
    );

    saveUsers(updatedUsers);
    setCurrentUser({ ...currentUser, email: newEmail, username: newUsername });
    alert('Profile updated successfully!');
    setCurrentPasswordForProfile('');
  };

  const handleChangePasswordWrapper = () => {
    handleChangePassword(currentPasswordForChange, newPass, confirmPass);
    setCurrentPasswordForChange('');
    setNewPass('');
    setConfirmPass('');
  };

  return (
    <div style={{ ...pageStyle, marginLeft: '260px' }}>
      <h2>Settings</h2>

      <div style={{ ...cardStyle, maxWidth: '500px' }}>
        <h3>Account: {currentUser?.email}</h3>
        <input
          type="text"
          placeholder="New Username"
          value={newUsername}
          onChange={e => setNewUsername(e.target.value)}
          style={inputStyle}
        /><br />
        <input
          type="email"
          placeholder="New Email"
          value={newEmail}
          onChange={e => setNewEmail(e.target.value)}
          style={inputStyle}
        /><br />
        <input
          type="password"
          placeholder="Current Password (required)"
          value={currentPasswordForProfile}
          onChange={e => setCurrentPasswordForProfile(e.target.value)}
          style={inputStyle}
        /><br />
        <button onClick={handleUpdateProfile} style={buttonStyle}>Update Profile</button>
      </div>

      <div style={{ ...cardStyle, marginTop: 30, maxWidth: '500px' }}>
        <h3>Change Password</h3>
        <input
          type="password"
          placeholder="Current Password"
          value={currentPasswordForChange}
          onChange={e => setCurrentPasswordForChange(e.target.value)}
          style={inputStyle}
        /><br />
        <input
          type="password"
          placeholder="New Password"
          value={newPass}
          onChange={e => setNewPass(e.target.value)}
          style={inputStyle}
        /><br />
        <input
          type="password"
          placeholder="Confirm New Password"
          value={confirmPass}
          onChange={e => setConfirmPass(e.target.value)}
          style={inputStyle}
        /><br />
        <button onClick={handleChangePasswordWrapper} style={buttonStyle}>Change Password</button>
      </div>
    </div>
  );
};
